me varchar(30) null, PRIMARY KEY (`id`),UNIQUE INDEX `id_UNIQUE` (`id` ASC) );CREATE TABLE infra.users ( id INT NOT NULL, cell varchar(20) null, createTime datetime null, guid varchar(30) null, password varchar(30) null, personalName varchar(20) null, PRIMARY KEY (`id`),UNIQUE INDEX `id_UNIQUE` (`id` ASC) );CREATE TABLE infra.user_devices ( id INT NOT NULL, created datetime null, device_id int null, is_logged int null, user_id int null, PRIMARY KEY (`id`),UNIQUE INDEX `id_UNIQUE` (`id` ASC) );CREATE TABLE infra.user_messages ( id INT NOT NULL, application varchar(100) null, device_id int null, email varchar(100) null, ip varchar(45) null, isRegister int null, message varchar(500) null, phone varchar(45) null, _dateTime date null, _name varchar(100) null, PRIMARY KEY (`id`),UNIQUE INDEX `id_UNIQUE` (`id` ASC) );CREATE TABLE infra.user_roles ( id INT NOT NULL, app_id int null, role_id int null, user_id int null, PRIMARY KEY (`id`),UNIQUE INDEX `id_UNIQUE` (`id` ASC) ); CREATE PROCEDURE infra.activeTrack ( device varchar(100),appname varchar(100),ip varchar(100),thekey varchar(100),thevalue varchar(1000),sp varchar(100)) BEGIN insert into infra.activetrack (created,device,appname,ip,thekey,thevalue,sp) values (now(),device,appname,ip,thekey,thevalue,sp); END; CREATE PROCEDURE infra.add_new_app ( device_name varchar(50),app_name varchar(30),description text) BEGIN declare counter int; select count(*) into counter from infra.applications where name = app_name; if counter > 0 then select 'false' as success,'app exists already' as message; else select count(*) into counter from infra.devices where name = device_name; if counter = 0 then select 'false' as success,'device does not exists' as message; else select id into counter from infra.devices where name = device_name; insert into infra.applications (name,description,device_created,created,permission,linkTo,myParentId,level,alias) values (app_name,description,counter,now(),'read',app_name,0,0,app_name); select id into cou