s success , @p2 as message,@p1 as RETURNED_SQLSTATE,'filter_update ' proc_name; ROLLBACK; END; call activeTrack( device_name,app_name,'','', '','clickItem'); insert into infra.clicks (device,appname,ip,item,_key,_value,_user,rowid,_dateTime) values (device_name,app_name,ip,_item,_key,_value,device_name,rowid,now()); SET @sql:=CONCAT('update devices_data.',app_name ,' set clicks=clicks+1 where id = ',rowid , '' ); call infra.log(@sql); PREPARE dynamic_statement FROM @sql; EXECUTE dynamic_statement; select 'true' as success; END; CREATE PROCEDURE infra.device_app ( device_name varchar(50),app_id int(11)) BEGIN declare counter int; declare dev_id int; declare app_name varchar(100); call infra.log('device_app '); set app_name = ''; set dev_id = 0; set counter= 0; select count(*) into counter from infra.devices dv where dv.name = device_name; if counter = 0 then select 'false' as success,'no device exists' as message; else set counter= 0; select count(*) into counter from infra.applications ap where ap.id = app_id ; if counter = 0 then select 'false' as success,'no application exists' as message; else select name into app_name from infra.applications where id = app_id; select id into dev_id from infra.devices dv where dv.name = device_name; set counter = 0; select count(*) into counter from infra.menu_apps ma where dev_id = ma.device_id and app_id = ma.app_id; if counter = 0 then insert into infra.menu_apps (app_id,device_id,app,mainApp,linkTo,myParentId,level,text) values (app_id,dev_id,app_name,'pageim',app_name,0,0,app_name); end if; /* not working */ /* insert into infra.menu_apps (app_id,device_id,app,alias,mainApp,permission,linkTo,icon,myParentId,level,text) values (app_id,dev_id,(select name,alias,mainApp,permission,linkTo,icon from infra.applications where id = app_id),0,0); */ select 'true' as success,id as table_id from infra.tables tbls where tbls.app_id = app_id ; end if; end if; END; CREATE PROCEDURE infra.device_menu ( device_name varchar(100),app_name varchar(100)) BEGIN declare devc_id int(11); declare appl_id int(11); declare counter int(11); select id into devc_id from infra.devices where name = device_name ; call activeTrack( device_name,app_name,'','', '','device_menu'); select count(*) into counter from infra.applications where name = app_name ; if counter = 1 then select id into appl_id from infra.applications where name = app_name ; call infra.device_app ( device_name ,appl_id); end if; select app, id, permission, linkTo, myParentId, icon, text, level, alias, mainApp, hasChild from infra.menu_apps m where m.device_id = devc_id and m.app_id in (select app_id from infra.menu_apps ma where ma.device_id in (select id from infra.devices dv where device_name = dv.name ) ); END; CREATE PROCEDURE infra.fields_of ( device_name varchar(50),app_name varchar(50),table_name varchar(50)) BEGIN declare counter int; declare the_table_id int; declare the_app_id int; DECLARE EXIT HANDLER FOR SQLEXCEPTION BEGIN GET DIAGNOSTICS CONDITION 1 @p1 = RETURNED_SQLSTATE, @p2 = MESSAGE_TEXT; SELECT 'false' as success , @p2 as message,@p1 as RETURNED_SQLSTATE,'state_update' proc_name; ROLLBACK; END; select id into the_table_id from infra.tables where name = table_name ; select * from infra.fields where table_id = the_table_id and is_active=1 ; END; CREATE PROCEDURE infra.field_add ( device_name varchar(50),table_id int(11),field_name varchar(50),field_type_id int(11),field_length int(11)) BEGIN declare counter int; declare the_table_name varchar(50); declare field_type_name varchar(50); DECLARE EXIT HANDLER FOR SQLEXCEPTION BEGIN GET DIAGNOSTICS CONDITION 1 @p1 = RETURNED_SQLSTATE, @p2 = MESSAGE_TEXT; SELECT 'false' as success , @p2 as message,@p1 as RETURNED_SQLSTATE,'state_update' proc_name; ROLLBACK; END; select count(*) into counter from infra.field_types where id = field_type_id ; if counter = 0 then select 'false' success,'the is no such field type' message; else select name into field_type_name from infra.field_types where id = field_type_id ; select count(*) into counter from infra.devices where device_name like name; if counter = 0 then select 'false' success,'the is no such field device' message; else select count(*) into counter from infra.tables where table_id = id; if counter = 0 then select 'false' success,'the is no such table' message; else select name into the_table_name from infra.tables where table_id = id; SET @sql:=CONCAT('insert into infra.fields (table_id,name,alias,field_type,field_length) values (',table_id,',',field_name,',',field_name,',',field_type_id ,',',field_length ,');' ); call infra.log(@sql); PREPARE dynamic_statement FROM @sql; EXECUTE dynamic_statement; DEALLOCATE PREPARE dynamic_statement; SET @sql:=CONCAT('ALTER TABLE devices_data.',the_table_name ,' ADD `',field_name, '` ' , field_type_name ,'(',field_length ,');' ); PREPARE dynamic_statement FROM @sql; EXECUTE dynamic_statement; DEALLOCATE PREPARE dynamic_statement; select 'true' success; end if; end if; end if; END; CREATE PROCEDURE infra.field_del ( device_name varchar(50),field_id int(11)) BEGIN declare counter int; declare the_table_id int; declare table_name varchar(100); declare col_name varchar(100); DECLARE EXIT HANDLER FOR SQLEXCEPTION BEGIN GET DIAGNOSTICS CONDITION 1 @p1 = RETURNED_SQLSTATE, @p2 = MESSAGE_TEXT; SELECT 'false' as success , @p2 as message,@p1 as RETURNED_SQLSTATE,'state_update' proc_name; ROLLBACK; END; select count(*) into counter from infra.fields where id = field_id ; if counter = 0 then select 'false' as success,'field not exists' as message; else select table_id into the_table_id from infra.fields where id = field_id ; select name into col_name from infra.fields where id = field_id ; select count(*) into counter from infra.tables where id = the_table_id ; if counter = 0 then select 'false' as success,'table not exists' as message; else select name into table_name from infra.tables where id = the_table_id ; delete from infra.fields where id = field_id ; SET @sql:=CONCAT('ALTER TABLE devices_data.',table_name,' drop column `',col_name ,'`;'); PREPARE dynamic_statement FROM @sql; EXECUTE dynamic_statement; DEALLOCATE PREPARE dynamic_statement; select 'true' as success; end if; end if; END; CREATE PROCEDURE infra.filter_update ( device_name varchar(50),app_name varchar(100),checked varchar(10),name varchar(100),value varchar(100),return_limit int(11)) BEGIN declare counter int(11); declare device_id int(11); declare app_id int(11); declare table_id int(11); DECLARE EXIT HANDLER FOR SQLEXCEPTION BEGIN call activeTrack( device_name,app_name,'',name, value,'filter_update'); GET DIAGNOSTICS CONDITION 1 @p1 = RETURNED_SQLSTATE, @p2 = MESSAGE_TEXT; SELECT 'false' as success , @p2 as message,@p1 as RETURNED_SQLSTATE,'filter_update ' proc_name; ROLLBACK; END; call infra.log('start filter_update '); call infra.log(device_name ); call infra.log(app_name); call infra.log(checked); call infra.log(name ); call infra.log(value ); set counter = 0; set device_id = 0; if name is null or name =" or value is null or value=" then select 'false' success,' empthy input filter_update' message; end if; select count(*) into counter from infra.devices as d where d.name like device_name ; if counter < 1 then select 'false' as success,'the deivce not exists 1' as message,counter ; else set counter = 0; select count(*) into counter from infra.applications as a where a.name like app_name ; if counter < 1 then select 'false' as success,'the app not exists 1' as message,counter ; else select a.id into app_id from infra.applications a where a.name like app_name ; select t.id into table_id from infra.tables t where t.app_id = app_id ; select id into device_id from infra.devices d where d.name = device_name; call infra.log('start work'); if name='ALL'and value='ALL' then delete from infra.filters where device_id = device_id and application = app_name ; else if checked ='false' then call infra.log('start delete'); delete from infra.filters where device_id = device_id and application = app_name and field= name and data=value; call infra.log('end delete'); else select count(*) into counter from infra.filters as f where f.device_id = device_id and f.application = app_name and f.field= name and f.data=value; call infra.log('counter'); call infra.log(counter); if counter < 1 then call infra.log('after insert into filter'); insert into infra.filters (device_id ,application ,data,field) values (device_id ,app_name ,value,name ); call infra.log('after insert filters'); end if; end if; end if; call infra.log('finish >>>>infra.filter_update'); call infra.search(device_name,app_name,return_limit ,0); end if; end if; END; CREATE PROCEDURE infra.getFilter ( device_name varchar(30),app_name varchar(50),limit_rows int(11)) BEGIN declare counter int(11); declare device_id int(11); declare table_id int(11); declare app_id int(11); declare tbl_name varchar(50); DECLARE done TINYINT DEFAULT FALSE; DECLARE var1 varchar(50) ; DECLARE statement varchar(5000) ; DECLARE statement1 varchar(5000) ; DECLARE statement2 varchar(5000) ; DECLARE cursor1 -- cursor1 CURSOR FOR SELECT name FROM infra.fields flds WHERE flds.application = app_name and flds.can_filter=1 and flds.is_active=1; DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE; DECLARE EXIT HANDLER FOR SQLEXCEPTION BEGIN call activeTrack( device_name,app_name,'','', '','getFilter'); GET DIAGNOSTICS CONDITION 1 @p1 = RETURNED_SQLSTATE, @p2 = MESSAGE_TEXT; SELECT 'false' as success , @p2 as message,@p1 as RETURNED_SQLSTATE,'state_update' proc_name; ROLLBACK; END; set counter = 0; /*handle filter*/ OPEN cursor1; my_loop: LOOP FETCH cursor1 INTO var1; IF done THEN LEAVE my_loop; ELSE if statement ='' or statement is null then set statement =concat( ' (select '',var1,'' as field ,',var1 ,' as data ,',' count(*) as count from devices_data.',app_name,' group by ',var1,' having (data is not null AND data !="" ) order by count desc limit ',limit_rows ,')'); else set statement =concat(statement , ' UNION All (select '',var1,'' as field ,',var1 ,' as data ,',' count(*) as count from devices_data.',app_name,' group by ',var1,' having (data is not null AND data !="" ) order by count desc limit ',limit_rows ,')'); end if; end if; END LOOP; CLOSE cursor1; call infra.log(statement ); /*handle selected filter*/ select a.id into app_id from infra.applications a where a.name like app_name ; select t.id into table_id from infra.tables t where t.app_id = app_id ; select id into device_id from infra.devices d where d.name = device_name; select count(*) into counter from infra.device_state as d where d.device_id = device_id ; if counter < 1 then insert into infra.device_state (device_id ,table_id) values (device_id ,table_id); set statement1 = 'select null from tables'; set statement2 = 'select null from tables'; else set statement1 =concat( 'select f.data,f.field from infra.filters as f where f.device_id = ',device_id,' and f.application = '',app_name,'''); /*set statement2 =concat( 'select f.name,f.label from infra.fields as f where f.application = '',app_name,'' and f.can_sort=1'); im not sure why this statment*/ set statement2 =concat( 'select f.name,f.label from infra.fields as f where f.application = '',app_name,'' and f.can_filter=1 and is_active=1'); end if; call infra.log('start stament'); call infra.log(statement); call infra.log(statement1); call infra.log(statement2); SET @sql:=statement ; SET @sql1:=statement1 ; SET @sql2:=statement2 ; PREPARE dynamic_statement FROM @sql; PREPARE dynamic_statement1 FROM @sql1; PREPARE dynamic_statement2 FROM @sql2; EXECUTE dynamic_statement; EXECUTE dynamic_statement1; EXECUTE dynamic_statement2; DEALLOCATE PREPARE dynamic_statement; DEALLOCATE PREPARE dynamic_statement1; DEALLOCATE PREPARE dynamic_statement2; END; CREATE PROCEDURE infra.getSort ( device_name varchar(50),app_name varchar(50)) BEGIN declare counter int(11); declare device_id int(11); declare table_id int(11); declare app_id int(11); declare tbl_name varchar(50); DECLARE done TINYINT DEFAULT FALSE; DECLARE var1 varchar(50) ; DECLARE statement varchar(5000) ; DECLARE statement2 varchar(5000) ; DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE; DECLARE EXIT HANDLER FOR SQLEXCEPTION BEGIN GET DIAGNOSTICS CONDITION 1 @p1 = RETURNED_SQLSTATE, @p2 = MESSAGE_TEXT; SELECT 'false' as success , @p2 as message,@p1 as RETURNED_SQLSTATE,'state_update' proc_name; ROLLBACK; END; call infra.log('start'); select a.id into app_id from infra.applications a where a.name like app_name ; select t.id into table_id from infra.tables t where t.app_id = app_id ; select id into device_id from infra.devices d where d.name = device_name; select count(*) into counter from infra.device_state as d where d.device_id = device_id ; if counter < 1 then insert into infra.device_state (device_id ,table_id) values (device_id ,table_id); set statement2 = 'select null from tables'; else set statement2 =concat( 'select f.name,f.label from infra.fields as f where f.application = '',app_name,'' and f.can_sort=1 and is_active=1'); end if; call infra.log('start stament'); call infra.log(statement2); SET @sql2:=statement2 ; PREPARE dynamic_statement2 FROM @sql2; EXECUTE dynamic_statement2; DEALLOCATE PREPARE dynamic_statement2; END; CREATE PROCEDURE infra.is_logged_in ( device_name varchar(50)) BEGIN declare counter int; declare dvc_id int; declare apassword varchar (10); declare user_cell varchar (30); select count(*) into counter from infra.devices where name = device_name ; select id into dvc_id from infra.devices where name = device_name ; select count(*) into counter from infra.user_devices where device_id = dvc_id and is_logged = 1 ; if counter = 1 then select user_id into dvc_id from infra.user_devices where device_id = dvc_id and is_logged = 1 ; select cell into user_cell from infra.users where id = dvc_id ; select 'true' as success , 'islogged in' as message,user_cell as cell ; else select 'false' as success , 'device has a user already' as message; end if ; END; CREATE 