n update user_devices set is_logged = 0 ,created=now() where device_id = dvc_id; select 'true' as success ,'log off success' message ; else select 'false' as success ,'logged off alrady' message ; end if; else select 'false' as success ,'device not exists' message ; end if; END; CREATE PROCEDURE infra.log_on ( device_name varchar(50),user_cell varchar(30),usr_psswrd varchar(30)) BEGIN declare counter int; declare dvc_id int; declare apassword varchar (30); declare usr_id int; declare delayMinutes int(11); call infra.log('logg on' ); set delayMinutes=100; select count(*) into counter from users where cell = user_cell and password = usr_psswrd ; if counter != 1 then select 'false' as success ,'user or password is wrong' message ; else select id into usr_id from users where cell = user_cell and password = usr_psswrd; select id into dvc_id from devices where name = device_name ; select count(*) into counter from users where cell = user_cell and password = usr_psswrd and createTime > (NOW() - INTERVAL delayMinutes MINUTE ); if counter != 1 then select 'false' as success ,'more then 1 minutes' message ; else /*update infra.users set isRegister=1 where cell = user_cell and password = usr_psswrd ;*/ select count(*) into counter from user_devices where user_id= usr_id and device_id=dvc_id; if counter =1 then update infra.user_devices set user_id= usr_id,is_logged=1 where device_id=dvc_id; select 'true' as success ,'log on success' message ; else insert into infra.user_devices (user_id,device_id,created,is_logged) values (usr_id,dvc_id,now(),1); select 'true' as success ,'log on success' message ; end if; end if; end if; END; CREATE PROCEDURE infra.menu_remove ( device_name varchar(50),p_app_id int(11)) BEGIN declare counter int; declare dev_id int; call infra.log(CONCAT (device_name,' ' , p_app_id) ); set dev_id = 0; select count(*) into dev_id from infra.devices dv where dv.name = device_name; call infra.log(CONCAT ('dev_id = ' , dev_id) ); if dev_id = 0 then select 'false'